import { createContext, useContext, useState, type ReactNode, useEffect } from 'react';
import { generateBRACUProof, loadCircuitFromUrl } from '../lib/zk-prover';

interface AuthContextType {
    isAuthenticated: boolean;
    isProving: boolean;
    token: string | null;
    login: (rawEmail: string) => Promise<void>;
    logout: () => void;
    error: string | null;
    circuitLoaded: boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
    const [token, setToken] = useState<string | null>(localStorage.getItem('zk_session'));
    const [isProving, setIsProving] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [circuitLoaded, setCircuitLoaded] = useState(false);

    const isAuthenticated = !!token;

    // Pre-load circuit on mount
    useEffect(() => {
        async function loadCircuit() {
            try {
                console.log('ðŸ“¦ Pre-loading ZK circuit...');
                await loadCircuitFromUrl('/circuits/bracu_verifier.json');
                setCircuitLoaded(true);
                console.log('âœ… Circuit loaded successfully');
            } catch (error) {
                console.error('âŒ Failed to load circuit:', error);
                setError(`Failed to load ZK circuit: ${error instanceof Error ? error.message : 'Unknown error'}`);
            }
        }
        loadCircuit();
    }, []);

    // Check valid session on load
    useEffect(() => {
        if (token) {
            fetch('/api/auth/session', {
                headers: { 'Authorization': `Bearer ${token}` }
            }).then(res => {
                if (!res.ok) logout();
            });
        }
    }, [token]);

    const login = async (rawEmail: string) => {
        if (!circuitLoaded) {
            setError('ZK circuit is still loading. Please wait a moment and try again.');
            return;
        }

        setIsProving(true);
        setError(null);
        try {
            console.log("Starting login process...");

            // Generate Proof using Noir Prover
            console.log("Generating proof with Noir...");
            const result = await generateBRACUProof(rawEmail, {
                maxHeaderLength: 2560
            });

            console.log("Proof generated!", result);

            // Send to Server
            // Backend expects: { proof: number[], publicInputs: string[] }
            const res = await fetch('/api/auth/verify', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    proof: result.proof,
                    publicInputs: result.publicInputs
                })
            });

            if (!res.ok) {
                const data = await res.json().catch(() => ({ error: res.statusText }));
                throw new Error(data.error || `Verification failed: ${res.status}`);
            }

            const { token } = await res.json();
            setToken(token);
            localStorage.setItem('zk_session', token);

        } catch (err: unknown) {
            console.error("Login failed:", err);
            let message = err instanceof Error ? err.message : 'Unknown error';
            
            // Provide user-friendly error messages for common issues
            if (message.includes('From header is in pre-hashed portion')) {
                message = 'This email format is not supported for ZK verification. Please try a different email or contact support.';
            } else if (message.includes('partial hash')) {
                message = 'Email processing failed. The email may be too large or have an unusual format.';
            } else if (message.includes('DKIM')) {
                message = 'Email signature verification failed. Please ensure the email has valid DKIM signatures.';
            } else if (message.includes('Circuit not loaded')) {
                message = 'ZK circuit not loaded. Please refresh the page and try again.';
            }
            
            setError(message);
        } finally {
            setIsProving(false);
        }
    };

    const logout = () => {
        setToken(null);
        localStorage.removeItem('zk_session');
    };

    return (
        <AuthContext.Provider value={{ isAuthenticated, isProving, token, login, logout, error, circuitLoaded }}>
            {children}
        </AuthContext.Provider>
    );
}

export function useAuth() {
    const context = useContext(AuthContext);
    if (context === undefined) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
}
